SELECT invoice.month as year_month, project.name, JSON_EXTRACT_SCALAR(TO_JSON_STRING(project.labels), \"$[0].value\")  as gcp_cost_codes , SUM(cost) + SUM(IFNULL(( SELECT SUM(c.amount) FROM UNNEST(credits) c), 0)) AS total, (SUM(CAST(cost * 1000000 AS int64)) + SUM(IFNULL(( SELECT SUM(CAST(c.amount * 1000000 AS int64)) FROM UNNEST(credits) c), 0))) / 1000000 AS total_exact FROM billing.gcp_billing_export_v1_013897_8670C0_F99FFA project_name where  invoice.month = \"yearmonth\" GROUP BY 1, 2, 3 ORDER BY 1 ASC, 2 ASC